INSERT INTO `article` (`article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `click`, `publish_time`, `thumb`) VALUES
(31, 10, 'mingshop目录结构介绍', '&lt;p&gt;&lt;img src=&quot;/Public/upload/article/2016/05-16/57389ea781868.png&quot; title=&quot;Mingshop目录结构.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;以上图可以看出 Mingshop目录结构非常简洁。&amp;nbsp; Application 下面 四个模块 分别是 Admin Api Home Mobile&amp;nbsp; 访问某个模块时是 域名/index.php/模块名称&lt;br/&gt;比如访问手机模块 www.ming.biz/index.php/Mobile &lt;br/&gt;Runtime 是mingshop 的整个缓存文件夹 缓存了一些临时数据， 其中 Html 是静态页面缓存。&amp;nbsp; 如果需要清空缓存点击管理员后台右上角清楚缓存。&lt;br/&gt;如果需要修改缓存时间 可以修改index.php 页面的 define(&amp;#39;TPSHOP_CACHE_TIME&amp;#39;,1); // Mingshop 缓存时间 把缓存时间改为 1秒钟&lt;br/&gt;静态页面文件在缓存在 Application\\Home\\Conf\\config.php 中配置， 看如下配置， 每行注释写的很清楚&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;#39;HTML_CACHE_RULES&amp;#39;&amp;nbsp;&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array(&amp;nbsp;&amp;nbsp;//&amp;nbsp;定义静态缓存规则\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;定义格式1&amp;nbsp;数组方式\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;#39;静态地址&amp;#39;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array(&amp;#39;静态规则&amp;#39;,&amp;nbsp;&amp;#39;有效期&amp;#39;,&amp;nbsp;&amp;#39;附加规则&amp;#39;),&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;index:index&amp;#39;=&amp;gt;array(&amp;#39;{:module}_{:controller}_{:action}&amp;#39;,TPSHOP_CACHE_TIME),&amp;nbsp;&amp;nbsp;//&amp;nbsp;首页静态缓存&amp;nbsp;TPSHOP_CACHE_TIME秒钟&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;#39;index:goodsList&amp;#39;=&amp;gt;array(&amp;#39;{:module}_{:controller}_{:action}&amp;#39;,300),&amp;nbsp;&amp;nbsp;//&amp;nbsp;列表页静态缓存&amp;nbsp;TPSHOP_CACHE_TIME秒钟&amp;nbsp;无参数&amp;nbsp;post&amp;nbsp;提交的很难缓存\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;index:goodsList&amp;#39;=&amp;gt;array(&amp;#39;{:module}_{:controller}_{:action}_{id}&amp;#39;,TPSHOP_CACHE_TIME),&amp;nbsp;&amp;nbsp;//&amp;nbsp;列表页静态缓存&amp;nbsp;TPSHOP_CACHE_TIME秒钟\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//ajax&amp;nbsp;请求的商品列表内容在&amp;nbsp;ajaxGoodsList&amp;nbsp;函数中&amp;nbsp;&amp;nbsp;S($keys,$html,300);&amp;nbsp;缓存\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;Goods:goodsInfo&amp;#39;=&amp;gt;array(&amp;#39;{:module}_{:controller}_{:action}_{id}&amp;#39;,TPSHOP_CACHE_TIME),&amp;nbsp;&amp;nbsp;//&amp;nbsp;商品详情页静态缓存&amp;nbsp;3秒钟&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;Goods:ajaxComment&amp;#39;=&amp;gt;array(&amp;#39;{:module}_{:controller}_{:action}_{goods_id}_{commentType}_{p}&amp;#39;,TPSHOP_CACHE_TIME),&amp;nbsp;&amp;nbsp;//&amp;nbsp;商品评论页静态缓存&amp;nbsp;TPSHOP_CACHE_TIME秒钟&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;Goods:ajaxConsult&amp;#39;=&amp;gt;array(&amp;#39;{:module}_{:controller}_{:action}_{goods_id}_{consult_type}_{p}&amp;#39;,TPSHOP_CACHE_TIME),&amp;nbsp;&amp;nbsp;//&amp;nbsp;商品咨询页静态缓存&amp;nbsp;TPSHOP_CACHE_TIME秒钟&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;商品详情的规格价格缓存在&amp;nbsp;ajaxGoodsPrice&amp;nbsp;函数里面&amp;nbsp;S($keys,$price,300);//&amp;nbsp;缓存数据300秒&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;channel:index&amp;#39;=&amp;gt;array(&amp;#39;{:module}_{:controller}_{:action}_{id}&amp;#39;,TPSHOP_CACHE_TIME),&amp;nbsp;&amp;nbsp;//&amp;nbsp;列表页静态缓存&amp;nbsp;TPSHOP_CACHE_TIME秒钟\r\n),&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;mingshop 上传的图片存贮在 \\Public\\upload&lt;br/&gt;mingshop 的插件目录在 \\plugins 里面包含 支付插件payment&amp;nbsp; 物流插件shipping&amp;nbsp; 已经登录插件login &amp;nbsp;&lt;br/&gt;mingshop 的模板在 \\Template\\pc&amp;nbsp; 和 \\Template\\mobile&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;mingshop 的公共配置文件目录 www\\Application\\Common 和 公共方法&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;先来看看后台的切换模板。 &lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;img src=&quot;/Public/upload/article/2016/05-16/57389f31799d7.png&quot; title=&quot;模板切换.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;看上图切换模板其实就是修改了 Application\\Home\\Conf\\html.php 文件后台修改模板然后观察这个文件有什么变化， 如果是linux 这个文件必须有 写入权限&lt;br/&gt;看以下代码。 &lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;array(\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;VIEW_PATH&amp;#39;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;gt;&amp;#39;./Template/pc/&amp;#39;,&amp;nbsp;//&amp;nbsp;改变某个模块的模板文件目录\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;DEFAULT_THEME&amp;#39;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;gt;&amp;#39;default&amp;#39;,&amp;nbsp;//&amp;nbsp;模板名称&amp;nbsp;切换模板&amp;nbsp;就是修改这里\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;TMPL_PARSE_STRING&amp;#39;&amp;nbsp;&amp;nbsp;=&amp;gt;array(\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;__PUBLIC__&