w/plugins/payment/unionpay/static/images/ui-bg_gloss-wave_35_f6a828_500x100.png\r\n/www/plugins/payment/unionpay/static/images/ui-bg_highlight-soft_100_eeeeee_1x100.png\r\n/www/plugins/payment/unionpay/static/images/ui-bg_highlight-soft_75_ffe45c_1x100.png\r\n/www/plugins/payment/unionpay/static/images/ui-icons_222222_256x240.png\r\n/www/plugins/payment/unionpay/static/images/ui-icons_228ef1_256x240.png\r\n/www/plugins/payment/unionpay/static/images/ui-icons_ef8c08_256x240.png\r\n/www/plugins/payment/unionpay/static/images/ui-icons_ffd27a_256x240.png\r\n/www/plugins/payment/unionpay/static/images/ui-icons_ffffff_256x240.png\r\n/www/plugins/payment/unionpay/static/jquery-1.11.2.min.js\r\n/www/plugins/payment/unionpay/static/jquery-ui.min.css&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;/span&gt;Mingshop1.3.0版将推出原生态 App IOS&lt;br/&gt;&lt;/p&gt;', '', '', '', 2, 1, 1463843939, '', 0, '', '', 3528, 1463760000, ''),
(199, 11, 'mingshop的功能插件', '&lt;p&gt;&lt;img style=&quot;width: 560px; height: 384px;&quot; src=&quot;/Public/upload/article/2016/05-22/5741d77a13c80.jpg&quot; title=&quot;2.jpg&quot; border=&quot;0&quot; height=&quot;384&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; width=&quot;560&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;插件千变万化， 一般插件安装卸载都会写份文档， 教开发者复制哪些文件到哪些目录， 然后执行哪些sql 新建哪些表。&lt;br/&gt;卸载插件的时候 也很麻烦需要一个个文件去寻找删除。&lt;br/&gt;但是mingshop 中的万能插件安装卸载 却很方便， 从此不需要像普通那么麻烦， 能做到一键安装 一键卸载， 前提条件是某些文件必须没有经过二次开发， 否则怕覆盖掉被开发的文件，&lt;br/&gt;所以在安装卸载插件的时候， 请注意将会覆盖哪些文件。 话不多说， 先看下mingshop 插件管理类的 如下安装代码&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;//如果是功能插件\r\nif($condition[&amp;#39;type&amp;#39;]&amp;nbsp;==&amp;nbsp;&amp;#39;function&amp;#39;)\r\n{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;include_once&amp;nbsp;&amp;nbsp;&amp;quot;plugins/function/{$condition[&amp;#39;code&amp;#39;]}/plugins.class.php&amp;quot;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$plugin&amp;nbsp;=&amp;nbsp;new&amp;nbsp;\\plugins();&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($update[&amp;#39;status&amp;#39;]&amp;nbsp;==&amp;nbsp;1)&amp;nbsp;//&amp;nbsp;安装\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$execute_sql&amp;nbsp;=&amp;nbsp;$plugin-&amp;gt;install_sql();&amp;nbsp;//&amp;nbsp;执行安装sql&amp;nbsp;语句\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$plugin-&amp;gt;install();&amp;nbsp;&amp;nbsp;//&amp;nbsp;执行&amp;nbsp;插件安装代码&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;else&amp;nbsp;//&amp;nbsp;卸载\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$execute_sql&amp;nbsp;=&amp;nbsp;$plugin-&amp;gt;uninstall_sql();&amp;nbsp;//&amp;nbsp;执行卸载sql&amp;nbsp;语句\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$plugin-&amp;gt;uninstall();&amp;nbsp;//&amp;nbsp;执行插件卸载代码&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$Model&amp;nbsp;=&amp;nbsp;new&amp;nbsp;\\Think\\Model();&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$Model-&amp;gt;execute($execute_sql);&amp;nbsp;//&amp;nbsp;执行插件所需的sql&amp;nbsp;语句\r\n}&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/Public/upload/article/2016/05-23/5741d79e8d8d8.jpg&quot; title=&quot;3.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;然后看看插件目录。 其中 www 是所需要用到覆盖的文件， &lt;br/&gt;config.php 插件配置&lt;br/&gt;install.sql 插件安装时用到的sql&lt;br/&gt;logo.jpg 插件图标&lt;br/&gt;plugins.class.php&amp;nbsp; 插件安装卸载累 格式固定的， 每个功能插件都必须有这个类， 名字格式都必须一致&lt;br/&gt;uninstall.sql 卸载所用到的文件&lt;br/&gt;下面不多说自己看代码体验一下&amp;nbsp; &lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\r\n&amp;nbsp;\r\nuse&amp;nbsp;Think\\Model\\RelationModel;\r\n/**\r\n&amp;nbsp;*&amp;nbsp;插件需要执行的方法&amp;nbsp;逻辑定义&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;*/\r\n\r\nclass&amp;nbsp;plugins&amp;nbsp;extends&amp;nbsp;RelationModel\r\n{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;$tableName&amp;nbsp;=&amp;nbsp;&amp;#39;plugin&amp;#39;;&amp;nbsp;//&amp;nbsp;插件表&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;$app_path;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;析构流函数\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;&amp;nbsp;__construct()&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;parent::__construct();&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;app_path&amp;nbsp;=&amp;nbsp;dirname($_SERVER[&amp;#39;SCRIPT_FILENAME&amp;#39;]).&amp;#39;/&amp;#39;;&amp;nbsp;//&amp;nbsp;当前项目路径&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;安装&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;function&amp;nbsp;install()\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;执行安装代码&amp;nbsp;&amp;nbsp;比如复制文件&amp;nbsp;&amp;nbsp;这里一般是将开发的文件&amp;nbsp;一个个&amp;nbsp;copy&amp;nbsp;到对应的目录中去\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;递归复制文件夹&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;recurse_copy($this-&amp;gt;app_path.&amp;#39;plugins/function/helloworld/www/&amp;#39;,$this-&amp;gt;app_path);&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;&amp;nbsp;卸载插件\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;function&amp;nbsp;uninstall()\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;执行卸载代码&amp;nbsp;&amp;nbsp;比如删除文件&amp;nbsp;&amp;nbsp;将安装时&amp;nbsp;复制好的&amp;nbsp;插件文件&amp;nbsp;&amp;nbsp;一个个删除掉\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;delFile($this-&amp;gt;app_path.&amp;#39;Application/Admin/Controller/HelloWorldController.class.php&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;delFile($this-&amp;gt;app_path.&amp;#39;Application/Admin//View/HelloWorld&amp;#39;);&amp;nbsp;//&amp;nbsp;删除HelloWorld目录下所有文件\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;rmdir($this-&amp;gt;app_path.&amp;#39;Application/Admin//View/HelloWorld&amp;#39;);&amp;nbsp;//&amp;nbsp;删除目录&amp;nbsp;HelloWorld&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;安装&amp;nbsp;sql&amp;nbsp;语句\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;这里的sql&amp;nbsp;可以的文件导入&amp;nbsp;也可以直接写死&amp;nbsp;插件要用到的新表&amp;nbsp;数据等\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;function&amp;nbsp;install_sql()\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$sql&amp;nbsp;=&amp;nbsp;file_get_contents($this-&amp;gt;app_path.&amp;#39;plugins/function/helloworld/install.sql&amp;#39;);&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$sql;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;卸载&amp;nbsp;sql&amp;nbsp;语句\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;把插件相关的数据删除掉.\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;function&amp;nbsp;uninstall_sql()\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$sql&amp;nbsp;=&amp;nbsp;file_get_contents($this-&amp;gt;app_path.&amp;#39;plugins/function/helloworld/uninstall.sql&amp;#39;);&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$sql;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n}&lt;/pre&gt;&lt;