(296, 32, '微信菜单管理添加错误 44002', '&lt;p&gt;&lt;img src=&quot;/Public/upload/article/2016/06-26/576fa6cd9d0f3.jpg&quot; style=&quot;float:none;&quot; title=&quot;1.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/Public/upload/article/2016/06-26/576fa6cdc7577.jpg&quot; style=&quot;float:none;&quot; title=&quot;2.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '896048934', '', '', 2, 1, 1466935006, '', 0, '', NULL, 59, 1466935006, ''),
(298, 32, '微信网页授权后调整参数错误', '&lt;p&gt;用户咋微信中登录授权后，跳转到原来的地址，代码中获取原来地址写法如下：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;if&amp;nbsp;(!isset($_GET[&amp;#39;code&amp;#39;])){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//触发微信返回code码\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$baseUrl&amp;nbsp;=&amp;nbsp;urlencode(&amp;#39;http://&amp;#39;.$_SERVER[&amp;#39;HTTP_HOST&amp;#39;].$_SERVER[&amp;#39;PHP_SELF&amp;#39;].$_SERVER[&amp;#39;QUERY_STRING&amp;#39;]);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$url&amp;nbsp;=&amp;nbsp;$this-&amp;gt;__CreateOauthUrlForCode($baseUrl);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Header(&amp;quot;Location:&amp;nbsp;$url&amp;quot;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit();&lt;/pre&gt;&lt;p&gt;如果这种方式返回，URL不带任何参数是正确的，如果用户在朋友圈打开分享的链接，就会系统报错，例如：当前的URL为：http://www.baidu.com/goods/goodsid/2.html，分享在朋友圈的链接加上了微信来源。http://www.baidu.com/goods/goodsid/2.html?from=singlemessage&amp;amp;is....使用$baseurl后，获取到的链接会变成http://www.baidu.com/goods/goodsid/2.htmlfrom=singlemessage&amp;amp;is....&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;注意：授权回来后的URL变了，缺少了个问号，如果逻辑处理的话，获取goodsid就会变成2.htmlfrom=singlemessage&amp;amp;is...，导致数据库报错，解决办法：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;private&amp;nbsp;function&amp;nbsp;get_url()&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$sys_protocal&amp;nbsp;=&amp;nbsp;isset($_SERVER[&amp;#39;SERVER_PORT&amp;#39;])&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;$_SERVER[&amp;#39;SERVER_PORT&amp;#39;]&amp;nbsp;==&amp;nbsp;&amp;#39;443&amp;#39;&amp;nbsp;?&amp;nbsp;&amp;#39;https://&amp;#39;&amp;nbsp;:&amp;nbsp;&amp;#39;http://&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$php_self&amp;nbsp;=&amp;nbsp;$_SERVER[&amp;#39;PHP_SELF&amp;#39;]&amp;nbsp;?&amp;nbsp;$_SERVER[&amp;#39;PHP_SELF&amp;#39;]&amp;nbsp;:&amp;nbsp;$_SERVER[&amp;#39;SCRIPT_NAME&amp;#39;];\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$path_info&amp;nbsp;=&amp;nbsp;isset($_SERVER[&amp;#39;PATH_INFO&amp;#39;])&amp;nbsp;?&amp;nbsp;$_SERVER[&amp;#39;PATH_INFO&amp;#39;]&amp;nbsp;:&amp;nbsp;&amp;#39;&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$relate_url&amp;nbsp;=&amp;nbsp;isset($_SERVER[&amp;#39;REQUEST_URI&amp;#39;])&amp;nbsp;?&amp;nbsp;$_SERVER[&amp;#39;REQUEST_URI&amp;#39;]&amp;nbsp;:&amp;nbsp;$php_self.(isset($_SERVER[&amp;#39;QUERY_STRING&amp;#39;])&amp;nbsp;?&amp;nbsp;&amp;#39;?&amp;#39;.$_SERVER[&amp;#39;QUERY_STRING&amp;#39;]&amp;nbsp;:&amp;nbsp;$path_info);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$sys_protocal.(isset($_SERVER[&amp;#39;HTTP_HOST&amp;#39;])&amp;nbsp;?&amp;nbsp;$_SERVER[&amp;#39;HTTP_HOST&amp;#39;]&amp;nbsp;:&amp;nbsp;&amp;#39;&amp;#39;).$relate_url;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;使用这个链接获取当前URL，就可以$this-&amp;gt;get_url();&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;在mobilebase控制器中，获取头像的函数还没有写，只有获取用户的openid，还需要几个函数&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;if($wechat_config&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;!$_SESSION[&amp;#39;openid&amp;#39;]){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//去授权获取openid\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$wxuser&amp;nbsp;=&amp;nbsp;$this-&amp;gt;GetOpenid();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//获取用户昵称\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//微信自动登录\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data&amp;nbsp;=&amp;nbsp;array(\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;openid&amp;#39;=&amp;gt;$wxuser[&amp;#39;openid&amp;#39;],//支付宝用户号\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;oauth&amp;#39;=&amp;gt;&amp;#39;weixin&amp;#39;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;nickname&amp;#39;=&amp;gt;$wxuser[&amp;#39;nickname&amp;#39;],\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;head_pic&amp;#39;=&amp;gt;$wxuser[&amp;#39;headimgurl&amp;#39;]\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$logic&amp;nbsp;=&amp;nbsp;new&amp;nbsp;UsersLogic();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data&amp;nbsp;=&amp;nbsp;$logic-&amp;gt;thirdLogin($data);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($data[&amp;#39;status&amp;#39;]&amp;nbsp;==&amp;nbsp;1){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;session(&amp;#39;user&amp;#39;,$data[&amp;#39;result&amp;#39;]);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;session(&amp;#39;user_id&amp;#39;,$data[&amp;#39;result&amp;#39;][&amp;#39;user_id&amp;#39;]);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;登录后将购物车的商品的&amp;nbsp;user_id&amp;nbsp;改为当前登录的id\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;M(&amp;#39;cart&amp;#39;)-&amp;gt;where(&amp;quot;session_id&amp;nbsp;=&amp;nbsp;&amp;#39;{$this-&amp;gt;session_id}&amp;#39;&amp;quot;)-&amp;gt;save(array(&amp;#39;user_id&amp;#39;=&amp;gt;$data[&amp;#39;result&amp;#39;][&amp;#39;user_id&amp;#39;]));\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;Getopenid()这个函数只是获取到了openid，并没有昵称与头像，需要在函数调用另外一个获取用户信息的函数：&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;public&amp;nbsp;function&amp;nbsp;GetOpenid()\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($_SESSION[&amp;#39;openid&amp;#39;])\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$_SESSION[&amp;#39;openid&amp;#39;];\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//通过code获得openid\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(!isset($_GET[&amp;#39;code&amp;#39;])){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//触发微信返回code码\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$baseUrl&amp;nbsp;=&amp;nbsp;urlencode(&amp;#39;http://&amp;#39;.$_SERVER[&amp;#39;HTTP_HOST&amp;#39;].$_SERVER[&amp;#39;PHP_SELF&amp;#39;].$_SERVER[&amp;#39;QUERY_STRING&amp;#39;]);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$url&amp;nbsp;=&amp;nbsp;$this-&amp;gt;__CreateOauthUrlForCode($baseUrl);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Header(&amp;quot;Location:&amp;nbsp;$url&amp;quot;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//获取code码，以获取openid\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$code&amp;nbsp;=&amp;nbsp;$_GET[&amp;#39;code&amp;#39;];\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data&amp;nbsp;=&amp;nbsp;$this-&amp;gt;getOpenidFromMp($code);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$openid&amp;nbsp;=&amp;nbsp;$data[&amp;#39;openid&amp;#39;];\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$_SESSION[&amp;#39;openid&amp;#39;]&amp;nbsp;=&amp;nbsp;$openid;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$userinfo=$this-&amp;gt;GetUserInfo($data[&amp;#39;access_token&amp;#39;],$openid);//获取用户头像等信息\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$userinfo;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;public&amp;nbsp;function&amp;nbsp;GetUserInfo($access_token,$openid){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$params&amp;nbsp;=&amp;nbsp;array(\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;access_token&amp;#39;&amp;nbsp;&amp;nbsp;=&amp;gt;&amp;nbsp;$access_token,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;openid&amp;#39;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;gt;&amp;nbsp;$openid,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;lang&amp;#39;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;#39;zh_CN&amp;#39;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$jsonStr&amp;nbsp;=&amp;nbsp;$this-&amp;gt;http(&amp;#39;https://api.weixin.qq.com/sns/userinfo&amp;#39;,&amp;nbsp;$params);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$jsonArr&amp;nbsp;=&amp;nbsp;$this-&amp;gt;parseJson($jsonStr);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($jsonArr)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$jsonArr;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;false;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;private&amp;nbsp;function&amp;nbsp;parseJson($json)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$jsonArr&amp;nbsp;=&amp;nbsp;json_decode($json,&amp;nbsp;true);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(isset($jsonArr[&amp;#39;errcode&amp;#39;]))&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($jsonArr[&amp;#39;errcode&amp;#39;]&amp;nbsp;==&amp;nbsp;0)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;result&amp;nbsp;=&amp;nbsp;$jsonArr;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;true;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//$this-&amp;gt;error&amp;nbsp;=&amp;nbsp;$this-&amp;gt;ErrorCode($jsonArr[&amp;#39;errcode&amp;#39;]);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;false;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}else&amp;nbsp;{&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$jsonArr;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;商品地方还有一些bug后面再一一添加。有开发的可以加我交流：li@cdhand.com&lt;/p&gt;', 'li@cdhand.com', '', '', 2, 1, 1466998432, '', 0, '', NULL, 164, 1466998432, ''),
(299, 30, 'curl_init扩展无法开启的问题', '&lt;p&gt;试了很多办法，找遍所有百度能找到的办法都解决不掉这个问题，安装的时候一直提示我curl_init不支持。求解决办法，用的wampserver apache2.2 php5.3.13 &amp;nbsp;win7 64位&lt;/p&gt;', '313962373', '', '', 2, 1, 1467010059, '', 0, '', NULL, 42, 1467010059, ''),
(300, 30, '关于ThinkPHP shop 支付宝浏览器提示问题', '&lt;p&gt;官方把支付宝的form表单上加了target=_blank导致很多浏览器都把支付页阻止了。&lt;img title=&quot;QQ图片20160628164008.png&quot; src=&quot;/Public/upload/article/2016/06-28/57723848ae00f.png&quot;/&gt;&lt;br/&gt;&lt;/p&gt;', '282477161', '', '', 2, 1, 1467103316, '', 0, '', '', 146, 1467043200, ''),
(303, 32, '申请退款退货，没有把商品规格键名（key）带上，导致购买同件商品不同规格时，只能退货一件（另外一件申请就会提示已经申请过了）', '&lt;p&gt;&lt;img src=&quot;/Public/upload/article/2016/06-29/577390be739eb.png&quot; style=&quot;float:none;&quot; title=&quot;前端页面.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/Public/upload/article/2016/06-29/577390beaab04.png&quot; style=&quot;float:none;&quot; title=&quot;后台页面.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/Public/upload/article/2016/06-29/577390bedac46.png&quot; style=&quot;float:none;&quot; title=&quot;数据表增加字段.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '549310369', '', '', 2, 1, 1467191622, '/Public/upload/article/2016-06-29/file_303.rar', 0, '', NULL, 94, 1467191622, ''),
(306, 31, '账号密码登录不了', '&lt;p&gt;前天改了数据库的表，昨天开始后台登录时原本的密码登录不了，请问如何重置后台密码？&lt;br/&gt;&lt;/p&gt;', '1073424127', '', '', 2, 1, 1467274277, '', 0, '', '', 86, 1467216000, ''),
(307, 31, 'IIS下安装Mingshop 后台登录提示验证码错误的问题', '&lt;p&gt;&amp;nbsp; &lt;img style=&quot;width: 450px; height: 326px;&quot; src=&quot;/Public/upload/article/2016/06-30/5774eb3c2cec2.png&quot; title=&quot;1.png&quot; border=&quot;0&quot; height=&quot;326&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; width=&quot;450&quot;/&gt;&lt;/p&gt;&lt;p&gt;验证码问题，解决方式，检查php.ini&amp;nbsp;session_savepath,检查该路径权限。&lt;br/&gt;&lt;br/&gt;将登陆跳转后的页面print_r($_SESSION),果然为空值。最后查找资料说是要把&amp;nbsp;use_trans_sid&amp;nbsp;的值改为&amp;nbsp;1，于是在配置文件中加了一行：&amp;#39;SESSION_OPTIONS&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;array(&amp;#39;use_trans_sid&amp;#39;=&amp;gt;1),然后再登陆的时候就好了。&lt;br/&gt;&lt;br/&gt;属于thinkphp&amp;nbsp;架构问题&lt;/p&gt;&lt;p&gt;改一下php.ini里的属性即可，iis下&lt;/p&gt;', '383387267', '', '', 2, 1, 1467280284, '', 0, '', '', 109, 1467216000, ''),
(308, 32, '初始安装不点选演示数据，也一样会安装。', '&lt;p&gt;初始安装不点选演示数据，安装后系统内一样存在演示数据，服务器和本地都测试过了，不知道大家有没？&lt;/p&gt;', '138589669', '', '', 2, 1, 1467294283, '', 0, '', NULL, 74, 1467294283, ''),
(309, 31, '关于Mingshop后台微信公众号发布菜单44002的问题', '&lt;p&gt;关于Mingshop后台微信公众号发布菜单44002的问题&lt;/p&gt;&lt;p&gt;因为在发布微信菜单时 用到了json_encode(&amp;#39;Mingshop商城&amp;#39;, JSON_UNESCAPED_UNICODE);&amp;nbsp;&amp;nbsp; 在5.3版本的php 不支持JSON_UNESCAPED_UNICODE&amp;nbsp; 把php版本 改成 5.4以上解决问题.&lt;br/&gt;&lt;/p&gt;', '', '', '', 2, 1, 1467369489, '', 0, '', '', 1177, 1467302400, ''),
(310, 30, '关于安装', '&lt;p&gt;有个问题，就是在安装第二步时，我的mysqli扩展未安装，但页面上没提示mysqli扩展未安装。&lt;br/&gt;&lt;/p&gt;', '232822071', '', '', 2, 1, 1467447356, '', 0, '', NULL, 55, 1467447356, ''),
(311, 30, '微信支付使用，测过几次1.2.4', '&lt;p&gt;微信支付无法使用，测过几次1.2.4&lt;/p&gt;&lt;p&gt;&lt;br/&gt;原因：public function getCode() 中直接用$_SESSION[&amp;#39;openid&amp;#39;]了，这个前面根本没有赋值，&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;而且OPENID也没有获取过来&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;另外扫描支付也是：&lt;/p&gt;&lt;p&gt;function get_code($order, $config_value)&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; {&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $notify_url = SITE_URL.&amp;#39;/index.php/Home/Payment/notifyUrl/pay_code/weixin&amp;#39;; // 接收微信支付异步通知回调地址，通知url必须为直接可访问的url，不能携带参数。&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //$notify_url = C(&amp;#39;site_url&amp;#39;).U(&amp;#39;Home/Payment/notifyUrl&amp;#39;,array(&amp;#39;pay_code&amp;#39;=&amp;gt;&amp;#39;weixin&amp;#39;)); // 接收微信支付异步通知回调地址，通知url必须为直接可访问的url，不能携带参数。&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //$notify_url = C(&amp;#39;site_url&amp;#39;).&amp;quot;/index.php?m=Home&amp;amp;c=Payment&amp;amp;a=notifyUrl&amp;amp;pay_code=weixin&amp;quot;;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $input = new WxPayUnifiedOrder();&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $input-&amp;gt;SetBody(&amp;quot;Mingshop商品&amp;quot;); // 商品描述&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $input-&amp;gt;SetAttach(&amp;quot;weixin&amp;quot;); // 附加数据，在查询API和支付通知中原样返回，该字段主要用于商户携带订单的自定义数据&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $input-&amp;gt;SetOut_trade_no($order[&amp;#39;order_sn&amp;#39;]); // 商户系统内部的订单号,32个字符内、可包含字母, 其他说明见商户订单号&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $input-&amp;gt;SetTotal_fee($order[&amp;#39;order_amount&amp;#39;]*100); // 订单总金额，单位为分，详见支付金额&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $input-&amp;gt;SetNotify_url($notify_url); // 接收微信支付异步通知回调地址，通知url必须为直接可访问的url，不能携带参数。&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $input-&amp;gt;SetTrade_type(&amp;quot;NATIVE&amp;quot;); // 交易类型&amp;nbsp;&amp;nbsp; 取值如下：JSAPI，NATIVE，APP，详细说明见参数规定&amp;nbsp;&amp;nbsp;&amp;nbsp; NATIVE--原生扫码支付&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $input-&amp;gt;SetProduct_id(&amp;quot;123456789&amp;quot;); // 商品ID trade_type=NATIVE，此参数必传。此id为二维码中包含的商品ID，商户自行定义。&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $notify = new NativePay();&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $result = $notify-&amp;gt;GetPayUrl($input); // 获取生成二维码的地址&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $url2 = $result[&amp;quot;code_url&amp;quot;];&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; return &amp;#39;&amp;lt;img alt=&amp;quot;模式二扫码支付&amp;quot; src=&amp;quot;/index.php?m=Home&amp;amp;c=Index&amp;amp;a=erweima&amp;amp;data=&amp;#39;.urlencode($url2).&amp;#39;&amp;quot; style=&amp;quot;width:110px;height:110px;&amp;quot;/&amp;gt;&amp;#39;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; }&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;br/&gt;&lt;/p&gt;&lt;p&gt;不能自定义商品描述和商品ID，更改后二维码就不显示了。&lt;/p&gt;', '2247173819', '', '', 2, 1, 1467516711, '', 0, '', '', 228, 1467475200, ''),
