(1037, 33, 'php将session写入数据库,多台服务器共享session', '&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\r\n\r\ndefine(&amp;#39;MY_SESS_TIME&amp;#39;,3600);&amp;nbsp;//SESSION&amp;nbsp;超时时间\r\n\r\n\r\nclass&amp;nbsp;My_Sess\r\n{\r\n\r\n&amp;nbsp;&amp;nbsp;static&amp;nbsp;public&amp;nbsp;$db;//数据库连接对象\r\n&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;function&amp;nbsp;__construct($dbname)\r\n&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;self::$db&amp;nbsp;=&amp;nbsp;$dbname;\r\n&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;function&amp;nbsp;init()\r\n&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$domain&amp;nbsp;=&amp;nbsp;&amp;#39;&amp;#39;;&amp;nbsp;//cookie&amp;nbsp;域名设置\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ini_set(&amp;#39;session.use_trans_sid&amp;#39;,0);//如果浏览器被禁止了使用cookie，这里设置为1后将会在url地址栏后面自动加上sessionid传递\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ini_set(&amp;#39;session.gc_maxlifetime&amp;#39;,MY_SESS_TIME);//这个是Session数据在服务器端储存的时间，如果超过这个时间，那么Session数据就自动删除！\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ini_set(&amp;#39;session.use_cookies&amp;#39;,1);&amp;nbsp;//sessionid的传递方式，默认是cookie，推荐使用\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ini_set(&amp;#39;session.cookie_path&amp;#39;,&amp;#39;/&amp;#39;);&amp;nbsp;//sessionid的cookie路径，不需要修改\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ini_set(&amp;#39;session.cookie_domain&amp;#39;,$domain);//&amp;nbsp;;sessionid\r\n的cookie域名，不需要修改&amp;nbsp;//多主机共享保存&amp;nbsp;SESSION&amp;nbsp;ID&amp;nbsp;的&amp;nbsp;COOKIE,因为我是本地服务器测试所以设置$domain=&amp;#39;&amp;#39;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;session_module_name(&amp;#39;user&amp;#39;);&amp;nbsp;//　//将&amp;nbsp;session.save_handler&amp;nbsp;设置为&amp;nbsp;user，而不是默认的&amp;nbsp;files\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//定义&amp;nbsp;SESSION&amp;nbsp;各项操作所对应的方法名&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//对应于类My_Sess的open()方法，下同\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;session_set_save_handler(\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array(&amp;#39;My_Sess&amp;#39;,&amp;#39;open&amp;#39;),\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array(&amp;#39;My_Sess&amp;#39;,&amp;#39;close&amp;#39;),&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array(&amp;#39;My_Sess&amp;#39;,&amp;#39;read&amp;#39;),&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array(&amp;#39;My_Sess&amp;#39;,&amp;#39;write&amp;#39;),&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array(&amp;#39;My_Sess&amp;#39;,&amp;#39;destroy&amp;#39;),&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array(&amp;#39;My_Sess&amp;#39;,&amp;#39;gc&amp;#39;)&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;session_start();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//session_start()必须位于session_set_save_handler方法之后\r\n&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;function&amp;nbsp;open($save_path,$session_name)\r\n&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;save_path=$save_path,session_name=$session_name&amp;lt;br/&amp;gt;&amp;quot;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;true;\r\n&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;function&amp;nbsp;close()\r\n&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;true;\r\n&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;function&amp;nbsp;read($sesskey)&amp;nbsp;//读取到对应于&amp;nbsp;SESSION&amp;nbsp;ID&amp;nbsp;的&amp;nbsp;SESSION&amp;nbsp;数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;nbsp;$sql&amp;nbsp;=&amp;nbsp;&amp;quot;SELECT&amp;nbsp;`data`&amp;nbsp;FROM&amp;nbsp;`sess`&amp;nbsp;WHERE&amp;nbsp;`sesskey`=&amp;#39;$sesskey&amp;#39;&amp;nbsp;AND&amp;nbsp;`expiry`&amp;gt;=&amp;quot;&amp;nbsp;.&amp;nbsp;time();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$res&amp;nbsp;=&amp;nbsp;self::$db-&amp;gt;getOne($sql);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$res&amp;nbsp;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;function&amp;nbsp;write($sesskey,$data)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$expiry&amp;nbsp;=&amp;nbsp;time()+MY_SESS_TIME;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$sql&amp;nbsp;=&amp;nbsp;&amp;quot;REPLACE&amp;nbsp;INTO&amp;nbsp;`sess`&amp;nbsp;(`sesskey`,`expiry`,`data`)&amp;nbsp;VALUES&amp;nbsp;(&amp;#39;$sesskey&amp;#39;,$expiry,&amp;#39;$data&amp;#39;)&amp;quot;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;self::$db-&amp;gt;query($sql);&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;true;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;function&amp;nbsp;destroy($sesskey)&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$sql&amp;nbsp;=&amp;nbsp;&amp;quot;DELETE&amp;nbsp;FROM&amp;nbsp;`sess`&amp;nbsp;WHERE&amp;nbsp;`sesskey`=&amp;#39;$sesskey&amp;#39;&amp;quot;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;self::$db-&amp;gt;query($sql);&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;true;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;function&amp;nbsp;gc($maxlifetime&amp;nbsp;=&amp;nbsp;null)&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;$sql&amp;nbsp;=&amp;nbsp;&amp;#39;DELETE&amp;nbsp;FROM&amp;nbsp;`sess`&amp;nbsp;WHERE&amp;nbsp;`expiry`&amp;lt;&amp;#39;.time();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;&amp;lt;br/&amp;gt;&amp;quot;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;self::$db-&amp;gt;query($sql);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//由于经常性的对表&amp;nbsp;sess&amp;nbsp;做删除操作，容易产生碎片,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//所以在垃圾回收中对该表进行优化操作。&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$sql&amp;nbsp;=&amp;nbsp;&amp;#39;OPTIMIZE&amp;nbsp;TABLE&amp;nbsp;`sess`&amp;#39;;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;self::$db-&amp;gt;query($sql);&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;true;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n}\r\n\r\n\r\nrequire(&amp;#39;cls_mysql.php&amp;#39;);\r\n\r\n$db_host&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;quot;127.0.0.1:3306&amp;quot;;\r\n$db_name&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;quot;test&amp;quot;;\r\n$db_user&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;quot;root&amp;quot;;\r\n$db_pass&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;quot;root&amp;quot;;\r\n\r\n$db&amp;nbsp;=&amp;nbsp;new&amp;nbsp;cls_mysql($db_host,&amp;nbsp;$db_user,&amp;nbsp;$db_pass,&amp;nbsp;$db_name);\r\n\r\n\r\n$sess&amp;nbsp;=&amp;nbsp;new&amp;nbsp;My_Sess($db);\r\n$sess-&amp;gt;init();\r\n\r\n&amp;nbsp;&amp;nbsp;$_SESSION[&amp;#39;a&amp;#39;]=&amp;#39;aaa&amp;#39;;\r\n\r\n$_SESSION[&amp;#39;b&amp;#39;]=&amp;#39;bbb&amp;#39;;\r\n\r\n$_SESSION[&amp;#39;c&amp;#39;]=&amp;#39;ccc&amp;#39;;\r\n\r\n$_SESSION[&amp;#39;d&amp;#39;]=array(&amp;#39;111&amp;#39;=&amp;gt;111,&amp;#39;2222&amp;#39;=&amp;gt;&amp;#39;2222&amp;#39;);\r\n\r\n&amp;nbsp;print_r($_SESSION);\r\n?&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '相亲', '', '', 2, 1, 1468811385, '/Public/upload/article/', 0, '', '', 217, 1468944000, ''),
